[
  {
    "name": "Task Completion",
    "definition": "Whether the agent successfully addresses all required components of the task prompt. Score 1 if all explicit requirements are met, 0 otherwise.",
    "scale": "Binary"
  },
  {
    "name": "Accuracy",
    "definition": "The correctness of factual information, calculations, data extraction, and logical reasoning in the response. Measured as the proportion of verifiable claims that are correct.",
    "scale": "Numeric [0-1]"
  },
  {
    "name": "Structural Coherence",
    "definition": "How well the response follows requested formats, organization, and presentation requirements. Evaluates adherence to specified output structure, sequencing, and formatting.",
    "scale": "Categorical {Low, Medium, High}"
  },
  {
    "name": "Process Validity",
    "definition": "Whether the agent demonstrates appropriate methodology, shows necessary work/reasoning, and follows logical steps toward the solution. Assesses the soundness of the approach taken.",
    "scale": "Categorical {Low, Medium, High}"
  },
  {
    "name": "Completeness",
    "definition": "The extent to which the response provides sufficient detail and coverage of the task requirements. Measured as the proportion of expected output elements that are adequately addressed.",
    "scale": "Numeric [0-1]"
  }
]
