[
  {
    "name": "Correctness",
    "definition": "Evaluate whether the output of the Agent matches the expected correct answer or format for the given task. Score 1 for a correct output and 0 for an incorrect output.",
    "scale": "Binary"
  },
  {
    "name": "Completeness",
    "definition": "Assess if the Agent's output includes all required components specified in the prompt. Score 1 if all components are present and 0 if any are missing.",
    "scale": "Binary"
  },
  {
    "name": "Clarity",
    "definition": "Rate the output based on how clearly the information is presented, including organization and readability. Use a scale of 0 (unclear) to 1 (very clear).",
    "scale": "Numeric"
  },
  {
    "name": "Relevance",
    "definition": "Determine if the content of the output is directly related to the task prompt and addresses the user's needs. Score from 0 (not relevant) to 1 (highly relevant).",
    "scale": "Numeric"
  },
  {
    "name": "Efficiency",
    "definition": "Evaluate the conciseness of the output, considering whether the Agent provided the necessary information without unnecessary elaboration. Score from 0 (inefficient) to 1 (very efficient).",
    "scale": "Numeric"
  }
]
